import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [divCount, setDivCount] = useState(0)
  const [array, setArray] = useState([0])

  const addToDivCount = () => {
    setDivCount(divCount + 1)
    var newArray = array.push(divCount)
    setArray(newArray)
  }

  return (
    <div className="App">
      <button onClick={addToDivCount}> Add div </button>
      <h1> {divCount} </h1>
      <div> {
        array.map(x => (
          <div> {x} </div>
        ))
      }

      </div>
    </div>
  );
}

export default App;
